	public static function doUpdate($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BaseHistoryTablePeer:doUpdate:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BaseHistoryTablePeer', $values, $con);
      if (false !== $ret)
